import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const TopBar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Determine if we should show back button (exclude main pages)
  const shouldShowBack = !['/tasks', '/chat', '/post', '/profile', '/achievements', '/'].includes(location.pathname);

  return (
    <div style={{
      backgroundColor: '#fff',
      padding: '15px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      borderBottom: '1px solid #eee',
      position: 'sticky',
      top: 0,
      zIndex: 100
    }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
        {shouldShowBack && (
          <button
            onClick={() => navigate(-1)}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '24px',
              color: '#333',
              cursor: 'pointer',
              padding: '5px'
            }}
          >
            ‚Üê
          </button>
        )}
        {!shouldShowBack && (
          <button
            onClick={() => navigate('/')}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '24px',
              color: '#333',
              cursor: 'pointer',
              padding: '5px'
            }}
          >
            üè†
          </button>
        )}
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <span style={{ fontSize: '24px', marginRight: '8px' }}>ü§ù</span>
          <span style={{ 
            fontSize: '28px',
            fontWeight: 'bold',
            background: 'linear-gradient(45deg, #007AFF, #00C6FF)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            Here4Help
          </span>
        </div>
      </div>

      <button
        onClick={() => navigate('/profile')}
        style={{
          background: 'none',
          border: 'none',
          fontSize: '24px',
          color: '#333',
          cursor: 'pointer',
          padding: '5px'
        }}
      >
        üë§
      </button>
    </div>
  );
};

export default TopBar;
