import { create } from 'zustand';
import { dummyTasks, dummyChats, dummyMessages } from '../data/dummyData';
import { Task, Chat } from '../types';

interface Achievement {
  id: string;
  title: string;
  description: string;
  progress: number;
  reward: number;
  icon: string;
}

interface Profile {
  name: string;
  wallet: number;
  achievements: Achievement[];
  taskHistory: Task[];
  rating: number;
}

interface TaskState {
  tasks: Task[];
  chats: Chat[];
  readMessages: string[];
  profile: Profile;
  chatMessages: Record<string, Chat[]>;
  addTask: (task: Omit<Task, 'id'>) => void;
  addChat: (chat: Chat) => { chats: Chat[], createdChat: Chat };
  markChatAsRead: (chatId: string) => void;
  addMessage: (chatId: string, message: Chat) => void;
  updateChat: (chatId: string, lastMessage: string, sender: string, timestamp?: string) => void;
  getChatMessages: (chatId: string) => Chat[];
}

// Add dummy profile data
const dummyProfile: Profile = {
  name: "John Doe",
  wallet: 6000,
  achievements: [
    {
      id: "1",
      title: "First Task Complete",
      description: "Completed your first task",
      progress: 100,
      reward: 50,
      icon: "ðŸŽ¯"
    },
    {
      id: "2",
      title: "Super Helper",
      description: "Helped 10 people",
      progress: 80,
      reward: 100,
      icon: "â­"
    },
    {
      id: "3",
      title: "Quick Responder",
      description: "Responded to 5 tasks within an hour",
      progress: 60,
      reward: 75,
      icon: "âš¡"
    }
  ],
  taskHistory: dummyTasks.slice(0, 3),
  rating: 4.8
};

// Add this helper function at the top of the file
const loadPersistedChats = () => {
  const savedChats = localStorage.getItem('chats');
  const savedMessages = localStorage.getItem('chatMessages');
  return {
    chats: savedChats ? JSON.parse(savedChats) : [],
    chatMessages: savedMessages ? JSON.parse(savedMessages) : {}
  };
};

// Get persisted data
const persistedData = loadPersistedChats();

export const useTaskStore = create<TaskState>((set, get) => ({
  tasks: dummyTasks,
  chats: persistedData.chats,
  readMessages: [],
  profile: dummyProfile,
  chatMessages: persistedData.chatMessages,
  addTask: (newTask) => set((state: TaskState) => ({
    tasks: [
      {
        ...newTask,
        id: String(state.tasks.length + 1),
        status: 'open'
      },
      ...state.tasks
    ]
  })),
  addChat: (newChat: Chat) => {
    let createdChat: Chat = newChat;
    
    set((state: TaskState) => {
      const existingChat = state.chats.find(chat => 
        chat.sender === newChat.sender && 
        chat.taskTitle === newChat.taskTitle
      );
      
      if (existingChat) {
        createdChat = existingChat;
        return state;
      }
      
      const newState = {
        ...state,
        chats: [newChat, ...state.chats],
        chatMessages: {
          ...state.chatMessages,
          [newChat.id]: []
        }
      };

      // Persist to localStorage
      localStorage.setItem('chats', JSON.stringify(newState.chats));
      localStorage.setItem('chatMessages', JSON.stringify(newState.chatMessages));
      
      return newState;
    });

    return { chats: get().chats, createdChat };
  },
  markChatAsRead: (chatId: string) => set((state: TaskState) => ({
    readMessages: [...state.readMessages, chatId],
    chats: state.chats.map(chat => 
      chat.id === chatId 
        ? { ...chat, unreadCount: 0 }
        : chat
    )
  })),
  addMessage: (chatId: string, message: Chat) => set((state: TaskState) => {
    const existingMessages = state.chatMessages[chatId] || [];
    const updatedMessages = [...existingMessages, message];
    
    const newState = {
      ...state,
      chatMessages: {
        ...state.chatMessages,
        [chatId]: updatedMessages
      },
      chats: state.chats.map(chat => 
        chat.id === chatId 
          ? { 
              ...chat, 
              message: message.message,
              timestamp: message.timestamp
            }
          : chat
      ).sort((a, b) => {
        const timeA = new Date(`1970/01/01 ${a.timestamp}`).getTime();
        const timeB = new Date(`1970/01/01 ${b.timestamp}`).getTime();
        return timeB - timeA;
      })
    };

    // Persist to localStorage
    localStorage.setItem('chats', JSON.stringify(newState.chats));
    localStorage.setItem('chatMessages', JSON.stringify(newState.chatMessages));

    return newState;
  }),
  updateChat: (chatId: string, lastMessage: string, sender: string, timestamp?: string) => set((state: TaskState) => {
    const currentTime = timestamp || new Date().toLocaleTimeString();
    const updatedChats = state.chats.map(chat => 
      chat.id === chatId 
        ? { 
            ...chat, 
            message: lastMessage,
            timestamp: currentTime
          }
        : chat
    );

    // Sort chats by timestamp (most recent first)
    const sortedChats = [...updatedChats].sort((a, b) => {
      const timeA = new Date(`1970/01/01 ${a.timestamp}`).getTime();
      const timeB = new Date(`1970/01/01 ${b.timestamp}`).getTime();
      return timeB - timeA;
    });

    const newState = {
      ...state,
      chats: sortedChats,
      chatMessages: state.chatMessages // Keep existing messages
    };

    // Persist to localStorage
    localStorage.setItem('chats', JSON.stringify(sortedChats));
    localStorage.setItem('chatMessages', JSON.stringify(state.chatMessages));

    return newState;
  }),
  getChatMessages: (chatId: string) => {
    const state = get();
    return state.chatMessages[chatId] || [];
  },
}));
