import React, { useState, CSSProperties } from 'react';
import { useTaskStore } from '../store/taskStore';
import DemoDisclaimer from '../components/DemoDisclaimer';
import TaskDetails from '../components/TaskDetails';
import { Task } from '../types';

const categories = [
  'All',
  'Finance',
  'Telecom',
  'Education',
  'Transport',
  'Shopping',
  'Food',
  'Healthcare',
  'Housing',
  'Government',
  'Entertainment',
  'Sports',
  'Technology',
  'Language',
  'Other'
];

// Add this style to hide scrollbars
const hideScrollbarStyle: CSSProperties = {
  display: 'flex', 
  gap: '10px', 
  overflowX: 'auto' as const,
  marginBottom: '20px',
  padding: '5px 0',
  msOverflowStyle: 'none',
  scrollbarWidth: 'none',
  WebkitOverflowScrolling: 'touch',
};

// Add a style tag to handle webkit scrollbar
const styleSheet = document.createElement('style');
styleSheet.textContent = `
  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }

  .category-scroll {
    display: flex;
    overflow-x: auto;
    padding-bottom: 10px;
    margin-bottom: 10px;
    -webkit-overflow-scrolling: touch;
  }

  .category-scroll::-webkit-scrollbar {
    height: 6px;
  }

  .category-scroll::-webkit-scrollbar-track {
    background: #333;
    border-radius: 3px;
  }

  .category-scroll::-webkit-scrollbar-thumb {
    background: #666;
    border-radius: 3px;
  }

  .category-scroll::-webkit-scrollbar-thumb:hover {
    background: #888;
  }
`;
document.head.appendChild(styleSheet);

const TaskBrowsing = () => {
  const tasks = useTaskStore((state) => state.tasks);
  const chats = useTaskStore((state) => state.chats);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);
  const [sortBy, setSortBy] = useState<'distance' | 'price' | 'language' | null>(null);
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');

  const filteredTasks = tasks.filter((task: Task) => 
    (selectedCategory === 'All' || task.category === selectedCategory) &&
    (task.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
     task.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
     task.location.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  // Function to check if we have contacted this task's poster
  const hasContactedPoster = (taskId: string) => {
    const task = tasks.find(t => t.id === taskId);
    if (!task) return false;
    
    return chats.some(chat => 
      chat.sender === task.postedBy && 
      chat.taskTitle === task.title
    );
  };

  // Sort tasks: contacted tasks go to bottom
  const sortedTasks = [...filteredTasks].sort((a, b) => {
    const aContacted = hasContactedPoster(a.id);
    const bContacted = hasContactedPoster(b.id);
    
    if (aContacted && !bContacted) return 1;
    if (!aContacted && bContacted) return -1;
    return 0;
  });

  // Add sorting controls
  const SortingControls = () => (
    <div style={{
      display: 'flex',
      gap: '10px',
      marginBottom: '15px',
      padding: '10px 0'
    }}>
      <select
        value={sortBy || ''}
        onChange={(e) => setSortBy(e.target.value as any)}
        style={{
          padding: '8px',
          borderRadius: '5px',
          border: '1px solid #ddd',
          backgroundColor: '#fff',
          color: '#333',
          cursor: 'pointer'
        }}
      >
        <option value="">Sort by...</option>
        <option value="distance">Distance</option>
        <option value="price">Price</option>
        <option value="language">Language</option>
      </select>

      <button
        onClick={() => setSortOrder(order => order === 'asc' ? 'desc' : 'asc')}
        style={{
          padding: '8px 12px',
          borderRadius: '5px',
          backgroundColor: '#fff',
          border: '1px solid #ddd',
          color: '#333',
          display: 'flex',
          alignItems: 'center',
          gap: '5px'
        }}
      >
        {sortOrder === 'asc' ? '‚Üë Ascending' : '‚Üì Descending'}
      </button>
    </div>
  );

  // Sort tasks based on selected criteria
  const getSortedTasks = (tasks: Task[]) => {
    if (!sortBy) return tasks;

    return [...tasks].sort((a, b) => {
      let comparison = 0;
      
      switch (sortBy) {
        case 'distance':
          // You might want to implement actual distance calculation here
          comparison = a.location.localeCompare(b.location);
          break;
        case 'price':
          comparison = a.price - b.price;
          break;
        case 'language':
          comparison = a.language.localeCompare(b.language);
          break;
      }

      return sortOrder === 'asc' ? comparison : -comparison;
    });
  };

  // Apply sorting to filtered tasks
  const sortedAndFilteredTasks = getSortedTasks(sortedTasks);

  return (
    <div style={{ 
      padding: '20px', 
      color: '#333',  // Changed to dark text for light theme
      backgroundColor: '#f5f5f5' 
    }}>
      <DemoDisclaimer />
      <div style={{ marginBottom: '20px' }}>
        <input 
          type="text" 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="üîç Search tasks..."
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #333',
            backgroundColor: '#222',
            color: 'white'
          }}
        />
      </div>

      <div style={{ 
        width: '100%',
        marginBottom: '20px'
      }}>
        <div className="category-scroll">
          <div style={{
            display: 'flex',
            gap: '8px',
            padding: '0 4px',
          }}>
            {categories.map(category => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                style={{
                  padding: '8px 16px',
                  borderRadius: '20px',
                  border: 'none',
                  backgroundColor: selectedCategory === category ? '#007AFF' : '#333',
                  color: 'white',
                  whiteSpace: 'nowrap',
                  cursor: 'pointer',
                  flexShrink: 0,
                  transition: 'background-color 0.2s'
                }}
              >
                {category}
              </button>
            ))}
          </div>
        </div>
      </div>

      <SortingControls />

      <div style={{ 
        height: '1px', 
        backgroundColor: '#333', 
        margin: '20px 0',
        opacity: 0.5 
      }} />

      <div 
        className="hide-scrollbar"
        style={{
          overflowY: 'scroll',
          maxHeight: 'calc(100vh - 250px)',
          paddingBottom: '80px',
          WebkitOverflowScrolling: 'touch'
        }}
      >
        {sortedAndFilteredTasks.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '20px', 
            color: '#999' 
          }}>
            No tasks found üò¢
          </div>
        ) : (
          sortedAndFilteredTasks.map((task) => (
            <div 
              key={task.id}
              onClick={() => setSelectedTask(task)}
              style={{
                backgroundColor: '#222',
                borderRadius: '10px',
                padding: '15px',
                marginBottom: '15px',
                cursor: 'pointer',
                opacity: hasContactedPoster(task.id) ? 0.6 : 1,
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h3 style={{ 
                  margin: 0,
                  color: 'white'
                }}>
                  {task.title}
                </h3>
                <span style={{ color: '#FFD700' }}>üí∞ ${task.price}</span>
              </div>
              <div style={{ 
                display: 'flex', 
                gap: '10px', 
                margin: '10px 0', 
                color: '#999'
              }}>
                <span>üìÇ {task.category}</span>
                <span>‚è∞ {task.time}</span>
              </div>
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                color: '#fff'
              }}>
                <span>üìç {task.location}</span>
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: '5px',
                  color: '#fff'
                }}>
                  <span>{task.language}</span>
                  {task.flag === 'us' ? 'üá∫üá∏' : task.flag === 'jp' ? 'üáØüáµ' : 'üåê'}
                </div>
              </div>
              {hasContactedPoster(task.id) && (
                <div style={{
                  backgroundColor: '#007AFF',
                  color: 'white',
                  padding: '4px 8px',
                  borderRadius: '4px',
                  fontSize: '12px',
                  marginTop: '8px'
                }}>
                  ‚úì Contacted
                </div>
              )}
            </div>
          ))
        )}
      </div>

      {selectedTask && (
        <TaskDetails
          task={selectedTask}
          onClose={() => setSelectedTask(null)}
        />
      )}
    </div>
  );
};

export default TaskBrowsing;
