import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const BottomNavigation = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const isActive = (path: string) => location.pathname === path;

  const iconStyle = {
    fontSize: '28px',
    marginBottom: '4px'
  };

  const labelStyle = {
    fontSize: '12px',
    marginTop: '4px'
  };

  return (
    <div style={{
      position: 'fixed',
      bottom: 0,
      left: 0,
      right: 0,
      height: '70px',
      backgroundColor: '#fff',
      display: 'flex',
      justifyContent: 'space-around',
      alignItems: 'center',
      borderTop: '1px solid #eee',
      maxWidth: '430px',
      margin: '0 auto',
      boxShadow: '0 -2px 10px rgba(0,0,0,0.05)'
    }}>
      <button
        onClick={() => navigate('/achievements')}
        style={{
          background: 'none',
          border: 'none',
          color: isActive('/achievements') ? '#007AFF' : '#666',
          cursor: 'pointer',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '60px'
        }}
      >
        <span style={iconStyle}>üèÜ</span>
        <span style={labelStyle}>Awards</span>
      </button>

      <button
        onClick={() => navigate('/tasks')}
        style={{
          background: 'none',
          border: 'none',
          color: isActive('/tasks') ? '#007AFF' : '#666',
          cursor: 'pointer',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '60px'
        }}
      >
        <span style={iconStyle}>üìã</span>
        <span style={labelStyle}>Tasks</span>
      </button>

      <button
        onClick={() => navigate('/post')}
        style={{
          background: 'none',
          border: 'none',
          color: isActive('/post') ? '#007AFF' : '#666',
          cursor: 'pointer',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '60px'
        }}
      >
        <span style={iconStyle}>‚úèÔ∏è</span>
        <span style={labelStyle}>Post</span>
      </button>

      <button
        onClick={() => navigate('/chat')}
        style={{
          background: 'none',
          border: 'none',
          color: isActive('/chat') ? '#007AFF' : '#666',
          cursor: 'pointer',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '60px'
        }}
      >
        <span style={iconStyle}>üí¨</span>
        <span style={labelStyle}>Chat</span>
      </button>

      <button
        onClick={() => navigate('/profile')}
        style={{
          background: 'none',
          border: 'none',
          color: isActive('/profile') ? '#007AFF' : '#666',
          cursor: 'pointer',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '60px'
        }}
      >
        <span style={iconStyle}>üë§</span>
        <span style={labelStyle}>Profile</span>
      </button>
    </div>
  );
};

export default BottomNavigation;
