import React from 'react';
import { dummyChats } from '../data/dummyData';

const ChatRoom = () => {
  return (
    <div style={{ padding: '20px', color: 'white' }}>
      <div style={{ marginBottom: '20px' }}>
        <input 
          type="text" 
          placeholder="Search..."
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #333',
            backgroundColor: '#222',
            color: 'white'
          }}
        />
      </div>

      {dummyChats.map((chat) => (
        <div 
          key={chat.id}
          style={{
            backgroundColor: '#222',
            borderRadius: '10px',
            padding: '15px',
            marginBottom: '15px'
          }}
        >
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
            <h3>{chat.sender}</h3>
            <span>{chat.taskTitle}</span>
            {chat.unreadCount > 0 && (
              <span style={{ 
                backgroundColor: '#007AFF',
                borderRadius: '50%',
                padding: '2px 8px'
              }}>
                {chat.unreadCount}
              </span>
            )}
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <p style={{ margin: 0 }}>{chat.message}</p>
            <span style={{ color: '#666' }}>{chat.timestamp}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ChatRoom;
