import React, { useState, CSSProperties } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTaskStore } from '../store/taskStore';
import DemoDisclaimer from '../components/DemoDisclaimer';
import LocationPicker from '../components/LocationPicker';

interface FormData {
  title: string;
  category: string;
  price: string;
  time: string;
  location: string;
  description: string;
  selectedDays: string[];
  startTime: string;
  endTime: string;
  language: string;
  [key: string]: string | string[];
}

const hideScrollbarStyle: CSSProperties = {
  padding: '20px', 
  color: 'white',
  paddingBottom: '80px',
  height: '100vh',
  overflowY: 'auto',
  msOverflowStyle: 'none' as const,  /* IE and Edge */
  scrollbarWidth: 'none' as const,   /* Firefox */
  WebkitOverflowScrolling: 'touch',  /* Smooth scrolling on iOS */
};

const styleSheet = document.createElement('style');
styleSheet.textContent = `
  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }
`;
document.head.appendChild(styleSheet);

const TaskPosting = () => {
  const navigate = useNavigate();
  const addTask = useTaskStore((state) => state.addTask);
  const [formData, setFormData] = useState<FormData>({
    title: '',
    category: '',
    price: '',
    time: '',
    location: '',
    description: '',
    selectedDays: [],
    startTime: '',
    endTime: '',
    language: 'English'
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    const isFormValid = Object.entries(formData).every(([key, value]) => {
      if (key === 'selectedDays') {
        return (value as string[]).length > 0;
      }
      if (typeof value === 'string') {
        return value.trim() !== '';
      }
      return true;
    });
    
    if (!isFormValid) {
      alert('Please fill in all fields');
      return;
    }

    if (formData.selectedDays.length === 0 || !formData.startTime || !formData.endTime) {
      alert('Please select days and time');
      return;
    }

    setIsLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    
    addTask({
      ...formData,
      price: Number(formData.price),
      postedBy: 'User',
      language: formData.language || 'English',
      flag: 'us'
    });
    
    setIsLoading(false);
    navigate('/tasks');
  };

  const CATEGORIES = [
    'Finance',
    'Telecom',
    'Education',
    'Transport',
    'Shopping',
    'Food',
    'Healthcare',
    'Other'
  ];

  const DAYS = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  const LANGUAGES = [
    { code: 'en', name: 'English', flag: 'us' },
    { code: 'jp', name: 'Êó•Êú¨Ë™û', flag: 'jp' }
  ];

  return (
    <div 
      className="hide-scrollbar"
      style={hideScrollbarStyle}
    >
      <DemoDisclaimer />
      <form onSubmit={handleSubmit}>
        {Object.keys(formData)
          .filter(field => !['selectedDays', 'startTime', 'endTime'].includes(field))
          .map((field) => (
            <div key={field} style={{ marginBottom: '15px' }}>
              <label style={{ display: 'block', marginBottom: '5px', textTransform: 'capitalize' }}>
                {field} {field === 'price' && 'üí∞'}
                {field === 'location' && 'üìç'}
                {field === 'time' && '‚è∞'}
              </label>
              {field === 'category' ? (
                <select
                  value={formData.category}
                  onChange={(e) => setFormData({
                    ...formData,
                    category: e.target.value
                  })}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #333',
                    backgroundColor: '#222',
                    color: 'white'
                  }}
                >
                  <option value="">Select a category</option>
                  {CATEGORIES.map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              ) : field === 'description' ? (
                <textarea
                  value={formData[field]}
                  onChange={(e) => setFormData({
                    ...formData,
                    [field]: e.target.value
                  })}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #333',
                    backgroundColor: '#222',
                    color: 'white',
                    minHeight: '100px',
                    resize: 'vertical'
                  }}
                />
              ) : field === 'time' ? (
                <div>
                  <div style={{ marginBottom: '10px' }}>
                    <div style={{ display: 'flex', gap: '5px', flexWrap: 'wrap' }}>
                      {DAYS.map(day => (
                        <button
                          type="button"
                          key={day}
                          onClick={() => {
                            const newDays = formData.selectedDays.includes(day)
                              ? formData.selectedDays.filter(d => d !== day)
                              : [...formData.selectedDays, day];
                            setFormData({
                              ...formData,
                              selectedDays: newDays,
                              time: `${newDays.join(', ')} ${formData.startTime}-${formData.endTime}`
                            });
                          }}
                          style={{
                            padding: '5px 10px',
                            backgroundColor: formData.selectedDays.includes(day) ? '#007AFF' : '#333',
                            border: 'none',
                            borderRadius: '5px',
                            color: 'white',
                            cursor: 'pointer'
                          }}
                        >
                          {day}
                        </button>
                      ))}
                    </div>
                  </div>
                  <div style={{ display: 'flex', gap: '10px' }}>
                    <input
                      type="time"
                      value={formData.startTime}
                      onChange={(e) => setFormData({
                        ...formData,
                        startTime: e.target.value,
                        time: `${formData.selectedDays.join(', ')} ${e.target.value}-${formData.endTime}`
                      })}
                      style={{
                        flex: 1,
                        padding: '10px',
                        backgroundColor: '#222',
                        border: '1px solid #333',
                        borderRadius: '5px',
                        color: 'white'
                      }}
                    />
                    <span style={{ alignSelf: 'center' }}>-</span>
                    <input
                      type="time"
                      value={formData.endTime}
                      onChange={(e) => setFormData({
                        ...formData,
                        endTime: e.target.value,
                        time: `${formData.selectedDays.join(', ')} ${formData.startTime}-${e.target.value}`
                      })}
                      style={{
                        flex: 1,
                        padding: '10px',
                        backgroundColor: '#222',
                        border: '1px solid #333',
                        borderRadius: '5px',
                        color: 'white'
                      }}
                    />
                  </div>
                </div>
              ) : field === 'location' ? (
                <LocationPicker
                  onLocationSelect={(location) => setFormData({
                    ...formData,
                    location
                  })}
                />
              ) : field === 'language' ? (
                <select
                  value={formData.language}
                  onChange={(e) => setFormData({
                    ...formData,
                    language: e.target.value,
                    flag: LANGUAGES.find(lang => lang.name === e.target.value)?.flag || 'us'
                  })}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #333',
                    backgroundColor: '#222',
                    color: 'white'
                  }}
                >
                  <option value="">Select a language</option>
                  {LANGUAGES.map(lang => (
                    <option key={lang.code} value={lang.name}>
                      {lang.name} {lang.flag === 'us' ? 'üá∫üá∏' : 'üáØüáµ'}
                    </option>
                  ))}
                </select>
              ) : (
                <input
                  required
                  type={field === 'price' ? 'number' : 'text'}
                  value={formData[field]}
                  onChange={(e) => setFormData({
                    ...formData,
                    [field]: e.target.value
                  })}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #333',
                    backgroundColor: '#222',
                    color: 'white'
                  }}
                />
              )}
            </div>
          ))}
        <button
          type="submit"
          disabled={isLoading}
          style={{
            width: '100%',
            padding: '15px',
            backgroundColor: isLoading ? '#666' : '#007AFF',
            border: 'none',
            borderRadius: '5px',
            color: 'white',
            fontSize: '16px',
            cursor: isLoading ? 'not-allowed' : 'pointer'
          }}
        >
          {isLoading ? 'Posting... ‚è≥' : 'POST üìù'}
        </button>
      </form>
    </div>
  );
};

export default TaskPosting;
