import React, { useState, useRef, useEffect } from 'react';
import { dummyChats } from '../data/dummyData';
import DemoDisclaimer from '../components/DemoDisclaimer';

interface ChatMessage {
  id: string;
  text: string;
  sender: string;
  timestamp: string;
}

const ChatRoom = () => {
  const [selectedChat, setSelectedChat] = useState<typeof dummyChats[0] | null>(null);
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Auto scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const initializeChat = (chat: typeof dummyChats[0]) => {
    setSelectedChat(chat);
    setMessages([
      {
        id: '1',
        text: chat.message,
        sender: chat.sender,
        timestamp: chat.timestamp
      }
    ]);
  };

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedChat) return;

    const message: ChatMessage = {
      id: Date.now().toString(),
      text: newMessage,
      sender: 'You',
      timestamp: new Date().toLocaleTimeString()
    };

    setMessages([...messages, message]);
    setNewMessage('');
  };

  return (
    <div style={{ 
      padding: '20px', 
      color: 'white', 
      height: '100vh',
      maxWidth: '430px',  // typical iPhone width
      margin: '0 auto',   // center the "phone"
      position: 'relative'
    }}>
      <DemoDisclaimer />
      
      {selectedChat ? (
        // Chat View
        <div style={{ 
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          display: 'flex',
          flexDirection: 'column',
          backgroundColor: '#111',
          maxWidth: '430px',
          margin: '0 auto'
        }}>
          {/* Chat Header */}
          <div style={{
            padding: '15px 20px',
            backgroundColor: '#222',
            display: 'flex',
            alignItems: 'center',
            borderBottom: '1px solid #333'
          }}>
            <button
              onClick={() => setSelectedChat(null)}
              style={{
                background: 'none',
                border: 'none',
                color: 'white',
                fontSize: '20px',
                marginRight: '15px',
                cursor: 'pointer'
              }}
            >
              ←
            </button>
            <h3 style={{ margin: 0, color: 'white' }}>{selectedChat.sender}</h3>
          </div>

          {/* Messages Container */}
          <div style={{
            flex: 1,
            overflowY: 'auto',
            padding: '20px',
            display: 'flex',
            flexDirection: 'column',
            gap: '10px'
          }}>
            {messages.map((msg) => (
              <div
                key={msg.id}
                style={{
                  alignSelf: msg.sender === 'You' ? 'flex-end' : 'flex-start',
                  maxWidth: '70%'
                }}
              >
                <div style={{
                  backgroundColor: msg.sender === 'You' ? '#007AFF' : '#333',
                  padding: '10px 15px',
                  borderRadius: '18px',
                  color: 'white'
                }}>
                  {msg.text}
                </div>
                <div style={{ 
                  fontSize: '12px', 
                  color: '#666',
                  marginTop: '5px',
                  textAlign: msg.sender === 'You' ? 'right' : 'left'
                }}>
                  {msg.timestamp}
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* Message Input */}
          <div style={{
            padding: '15px 20px',
            backgroundColor: '#222',
            borderTop: '1px solid #333'
          }}>
            <form onSubmit={handleSendMessage} style={{ display: 'flex', gap: '10px' }}>
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type a message..."
                style={{
                  flex: 1,
                  padding: '12px',
                  borderRadius: '20px',
                  border: '1px solid #333',
                  backgroundColor: '#333',
                  color: 'white',
                  fontSize: '16px'
                }}
              />
              <button
                type="submit"
                disabled={!newMessage.trim()}
                style={{
                  padding: '12px 20px',
                  backgroundColor: '#007AFF',
                  border: 'none',
                  borderRadius: '20px',
                  color: 'white',
                  cursor: newMessage.trim() ? 'pointer' : 'not-allowed',
                  opacity: newMessage.trim() ? 1 : 0.5
                }}
              >
                Send
              </button>
            </form>
          </div>
        </div>
      ) : (
        // Chat List View
        <div>
          <div style={{ marginBottom: '20px' }}>
            <input 
              type="text" 
              placeholder="Search..."
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: '5px',
                border: '1px solid #333',
                backgroundColor: '#222',
                color: 'white'
              }}
            />
          </div>

          {dummyChats.map((chat) => (
            <div 
              key={chat.id}
              onClick={() => initializeChat(chat)}
              style={{
                backgroundColor: '#222',
                borderRadius: '10px',
                padding: '15px',
                marginBottom: '15px',
                cursor: 'pointer'
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
                <h3 style={{ margin: 0 }}>{chat.sender}</h3>
                <span>{chat.taskTitle}</span>
                {chat.unreadCount > 0 && (
                  <span style={{ 
                    backgroundColor: '#007AFF',
                    borderRadius: '50%',
                    padding: '2px 8px'
                  }}>
                    {chat.unreadCount}
                  </span>
                )}
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <p style={{ margin: 0 }}>{chat.message}</p>
                <span style={{ color: '#666' }}>{chat.timestamp}</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ChatRoom;
