import React, { useState, CSSProperties } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTaskStore } from '../store/taskStore';
import DemoDisclaimer from '../components/DemoDisclaimer';
import LocationPicker from '../components/LocationPicker';

interface FormData {
  title: string;
  category: string;
  price: string;
  time: string;
  location: string;
  description: string;
  selectedDays: string[];
  startTime: string;
  endTime: string;
  language: string;
  country: string;
  [key: string]: string | string[];
}

const hideScrollbarStyle: CSSProperties = {
  padding: '20px', 
  color: 'white',
  paddingBottom: '80px',
  height: '100vh',
  overflowY: 'auto',
  msOverflowStyle: 'none' as const,  /* IE and Edge */
  scrollbarWidth: 'none' as const,   /* Firefox */
  WebkitOverflowScrolling: 'touch',  /* Smooth scrolling on iOS */
};

const styleSheet = document.createElement('style');
styleSheet.textContent = `
  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }
`;
document.head.appendChild(styleSheet);

const COUNTRIES = [
  { code: 'AF', name: 'Afghanistan', flag: 'üá¶üá´' },
  { code: 'AL', name: 'Albania', flag: 'üá¶üá±' },
  { code: 'DZ', name: 'Algeria', flag: 'üá©üáø' },
  { code: 'AR', name: 'Argentina', flag: 'üá¶üá∑' },
  { code: 'AU', name: 'Australia', flag: 'üá¶üá∫' },
  { code: 'AT', name: 'Austria', flag: 'üá¶üáπ' },
  { code: 'BD', name: 'Bangladesh', flag: 'üáßüá©' },
  { code: 'BE', name: 'Belgium', flag: 'üáßüá™' },
  { code: 'BR', name: 'Brazil', flag: 'üáßüá∑' },
  { code: 'CA', name: 'Canada', flag: 'üá®üá¶' },
  { code: 'CL', name: 'Chile', flag: 'üá®üá±' },
  { code: 'CN', name: 'China', flag: 'üá®üá≥' },
  { code: 'CO', name: 'Colombia', flag: 'üá®üá¥' },
  { code: 'DK', name: 'Denmark', flag: 'üá©üá∞' },
  { code: 'EG', name: 'Egypt', flag: 'üá™üá¨' },
  { code: 'FI', name: 'Finland', flag: 'üá´üáÆ' },
  { code: 'FR', name: 'France', flag: 'üá´üá∑' },
  { code: 'DE', name: 'Germany', flag: 'üá©üá™' },
  { code: 'GR', name: 'Greece', flag: 'üá¨üá∑' },
  { code: 'HK', name: 'Hong Kong', flag: 'üá≠üá∞' },
  { code: 'IN', name: 'India', flag: 'üáÆüá≥' },
  { code: 'ID', name: 'Indonesia', flag: 'üáÆüá©' },
  { code: 'IR', name: 'Iran', flag: 'üáÆüá∑' },
  { code: 'IQ', name: 'Iraq', flag: 'üáÆüá∂' },
  { code: 'IE', name: 'Ireland', flag: 'üáÆüá™' },
  { code: 'IL', name: 'Israel', flag: 'üáÆüá±' },
  { code: 'IT', name: 'Italy', flag: 'üáÆüáπ' },
  { code: 'JP', name: 'Japan', flag: 'üáØüáµ' },
  { code: 'KR', name: 'Korea', flag: 'üá∞üá∑' },
  { code: 'MY', name: 'Malaysia', flag: 'üá≤üáæ' },
  { code: 'MX', name: 'Mexico', flag: 'üá≤üáΩ' },
  { code: 'NL', name: 'Netherlands', flag: 'üá≥üá±' },
  { code: 'NZ', name: 'New Zealand', flag: 'üá≥üáø' },
  { code: 'NO', name: 'Norway', flag: 'üá≥üá¥' },
  { code: 'PK', name: 'Pakistan', flag: 'üáµüá∞' },
  { code: 'PE', name: 'Peru', flag: 'üáµüá™' },
  { code: 'PH', name: 'Philippines', flag: 'üáµüá≠' },
  { code: 'PL', name: 'Poland', flag: 'üáµüá±' },
  { code: 'PT', name: 'Portugal', flag: 'üáµüáπ' },
  { code: 'RU', name: 'Russia', flag: 'üá∑üá∫' },
  { code: 'SA', name: 'Saudi Arabia', flag: 'üá∏üá¶' },
  { code: 'SG', name: 'Singapore', flag: 'üá∏üá¨' },
  { code: 'ZA', name: 'South Africa', flag: 'üáøüá¶' },
  { code: 'ES', name: 'Spain', flag: 'üá™üá∏' },
  { code: 'SE', name: 'Sweden', flag: 'üá∏üá™' },
  { code: 'CH', name: 'Switzerland', flag: 'üá®üá≠' },
  { code: 'TW', name: 'Taiwan', flag: 'üáπüáº' },
  { code: 'TH', name: 'Thailand', flag: 'üáπüá≠' },
  { code: 'TR', name: 'Turkey', flag: 'üáπüá∑' },
  { code: 'UA', name: 'Ukraine', flag: 'üá∫üá¶' },
  { code: 'AE', name: 'United Arab Emirates', flag: 'üá¶üá™' },
  { code: 'GB', name: 'United Kingdom', flag: 'üá¨üáß' },
  { code: 'US', name: 'United States', flag: 'üá∫üá∏' },
  { code: 'VN', name: 'Vietnam', flag: 'üáªüá≥' }
].sort((a, b) => a.name.localeCompare(b.name));

const TaskPosting = () => {
  const navigate = useNavigate();
  const addTask = useTaskStore((state) => state.addTask);
  const [formData, setFormData] = useState<FormData>({
    title: '',
    category: '',
    price: '',
    time: '',
    location: '',
    description: '',
    selectedDays: [],
    startTime: '',
    endTime: '',
    language: 'English',
    country: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    const isFormValid = Object.entries(formData).every(([key, value]) => {
      if (key === 'selectedDays') {
        return (value as string[]).length > 0;
      }
      if (typeof value === 'string') {
        return value.trim() !== '';
      }
      return true;
    });
    
    if (!isFormValid) {
      alert('Please fill in all fields');
      return;
    }

    if (formData.selectedDays.length === 0 || !formData.startTime || !formData.endTime) {
      alert('Please select days and time');
      return;
    }

    setIsLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    
    addTask({
      ...formData,
      price: Number(formData.price),
      postedBy: 'User',
      language: formData.language || 'English',
      flag: 'us',
      status: 'open'
    });
    
    setIsLoading(false);
    navigate('/tasks');
  };

  const CATEGORIES = [
    'Finance',
    'Telecom',
    'Education',
    'Transport',
    'Shopping',
    'Food',
    'Healthcare',
    'Other'
  ];

  const DAYS = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  const LANGUAGES = [
    { code: 'ar', name: 'Arabic', flag: 'üá¶üá™' },
    { code: 'bn', name: 'Bengali', flag: 'üáßüá©' },
    { code: 'zh', name: 'Chinese', flag: 'üá®üá≥' },
    { code: 'en', name: 'English', flag: 'üá∫üá∏' },
    { code: 'fr', name: 'French', flag: 'üá´üá∑' },
    { code: 'de', name: 'German', flag: 'üá©üá™' },
    { code: 'hi', name: 'Hindi', flag: 'üáÆüá≥' },
    { code: 'id', name: 'Indonesian', flag: 'üáÆüá©' },
    { code: 'it', name: 'Italian', flag: 'üáÆüáπ' },
    { code: 'ja', name: 'Japanese', flag: 'üáØüáµ' },
    { code: 'ko', name: 'Korean', flag: 'üá∞üá∑' },
    { code: 'ms', name: 'Malay', flag: 'üá≤üáæ' },
    { code: 'pt', name: 'Portuguese', flag: 'üáµüáπ' },
    { code: 'ru', name: 'Russian', flag: 'üá∑üá∫' },
    { code: 'es', name: 'Spanish', flag: 'üá™üá∏' },
    { code: 'th', name: 'Thai', flag: 'üáπüá≠' },
    { code: 'tr', name: 'Turkish', flag: 'üáπüá∑' },
    { code: 'ur', name: 'Urdu', flag: 'üáµüá∞' },
    { code: 'vi', name: 'Vietnamese', flag: 'üáªüá≥' }
  ].sort((a, b) => a.name.localeCompare(b.name));

  return (
    <div 
      className="hide-scrollbar"
      style={hideScrollbarStyle}
    >
      <DemoDisclaimer />
      <form onSubmit={handleSubmit}>
        {Object.keys(formData)
          .filter(field => !['selectedDays', 'startTime', 'endTime'].includes(field))
          .map((field) => (
            <div key={field} style={{ marginBottom: '15px' }}>
              <label style={{ display: 'block', marginBottom: '5px', textTransform: 'capitalize' }}>
                {field} {field === 'price' && 'üí∞'}
                {field === 'location' && 'üìç'}
                {field === 'time' && '‚è∞'}
              </label>
              {field === 'category' ? (
                <select
                  value={formData.category}
                  onChange={(e) => setFormData({
                    ...formData,
                    category: e.target.value
                  })}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #333',
                    backgroundColor: '#222',
                    color: 'white'
                  }}
                >
                  <option value="">Select a category</option>
                  {CATEGORIES.map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              ) : field === 'description' ? (
                <textarea
                  value={formData[field]}
                  onChange={(e) => setFormData({
                    ...formData,
                    [field]: e.target.value
                  })}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #333',
                    backgroundColor: '#222',
                    color: 'white',
                    minHeight: '100px',
                    resize: 'vertical'
                  }}
                />
              ) : field === 'time' ? (
                <div>
                  <div style={{ marginBottom: '10px' }}>
                    <div style={{ display: 'flex', gap: '5px', flexWrap: 'wrap' }}>
                      {DAYS.map(day => (
                        <button
                          type="button"
                          key={day}
                          onClick={() => {
                            const newDays = formData.selectedDays.includes(day)
                              ? formData.selectedDays.filter(d => d !== day)
                              : [...formData.selectedDays, day];
                            setFormData({
                              ...formData,
                              selectedDays: newDays,
                              time: `${newDays.join(', ')} ${formData.startTime}-${formData.endTime}`
                            });
                          }}
                          style={{
                            padding: '5px 10px',
                            backgroundColor: formData.selectedDays.includes(day) ? '#007AFF' : '#333',
                            border: 'none',
                            borderRadius: '5px',
                            color: 'white',
                            cursor: 'pointer'
                          }}
                        >
                          {day}
                        </button>
                      ))}
                    </div>
                  </div>
                  <div style={{ display: 'flex', gap: '10px' }}>
                    <input
                      type="time"
                      value={formData.startTime}
                      onChange={(e) => setFormData({
                        ...formData,
                        startTime: e.target.value,
                        time: `${formData.selectedDays.join(', ')} ${e.target.value}-${formData.endTime}`
                      })}
                      style={{
                        flex: 1,
                        padding: '10px',
                        backgroundColor: '#222',
                        border: '1px solid #333',
                        borderRadius: '5px',
                        color: 'white'
                      }}
                    />
                    <span style={{ alignSelf: 'center' }}>-</span>
                    <input
                      type="time"
                      value={formData.endTime}
                      onChange={(e) => setFormData({
                        ...formData,
                        endTime: e.target.value,
                        time: `${formData.selectedDays.join(', ')} ${formData.startTime}-${e.target.value}`
                      })}
                      style={{
                        flex: 1,
                        padding: '10px',
                        backgroundColor: '#222',
                        border: '1px solid #333',
                        borderRadius: '5px',
                        color: 'white'
                      }}
                    />
                  </div>
                </div>
              ) : field === 'location' ? (
                <LocationPicker
                  onLocationSelect={(location) => setFormData({
                    ...formData,
                    location
                  })}
                />
              ) : field === 'language' ? (
                <div style={{ position: 'relative' }}>
                  <input
                    list="languages"
                    value={formData.language}
                    onChange={(e) => {
                      const selectedLang = LANGUAGES.find(lang => 
                        lang.name.toLowerCase() === e.target.value.toLowerCase()
                      );
                      setFormData({
                        ...formData,
                        language: e.target.value,
                        flag: selectedLang?.flag || 'us'
                      });
                    }}
                    placeholder="Type or select a language..."
                    style={{
                      width: '100%',
                      padding: '10px',
                      borderRadius: '5px',
                      border: '1px solid #333',
                      backgroundColor: '#222',
                      color: 'white'
                    }}
                  />
                  <datalist id="languages">
                    {LANGUAGES.map(lang => (
                      <option key={lang.code} value={lang.name}>
                        {lang.name} {lang.flag}
                      </option>
                    ))}
                  </datalist>
                </div>
              ) : field === 'country' ? (
                <div style={{ position: 'relative' }}>
                  <input
                    list="countries"
                    value={formData.country}
                    onChange={(e) => {
                      const selectedCountry = COUNTRIES.find(country => 
                        country.name.toLowerCase() === e.target.value.toLowerCase()
                      );
                      setFormData({
                        ...formData,
                        country: e.target.value,
                        flag: selectedCountry?.flag || 'üåê'
                      });
                    }}
                    placeholder="Type or select a country..."
                    style={{
                      width: '100%',
                      padding: '10px',
                      borderRadius: '5px',
                      border: '1px solid #333',
                      backgroundColor: '#222',
                      color: 'white'
                    }}
                  />
                  <datalist id="countries">
                    {COUNTRIES.map(country => (
                      <option key={country.code} value={country.name}>
                        {country.name} {country.flag}
                      </option>
                    ))}
                  </datalist>
                </div>
              ) : (
                <input
                  required
                  type={field === 'price' ? 'number' : 'text'}
                  value={formData[field]}
                  onChange={(e) => setFormData({
                    ...formData,
                    [field]: e.target.value
                  })}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #333',
                    backgroundColor: '#222',
                    color: 'white'
                  }}
                />
              )}
            </div>
          ))}
        <button
          type="submit"
          disabled={isLoading}
          style={{
            width: '100%',
            padding: '15px',
            backgroundColor: isLoading ? '#666' : '#007AFF',
            border: 'none',
            borderRadius: '5px',
            color: 'white',
            fontSize: '16px',
            cursor: isLoading ? 'not-allowed' : 'pointer'
          }}
        >
          {isLoading ? 'Posting... ‚è≥' : 'POST üìù'}
        </button>
      </form>
    </div>
  );
};

export default TaskPosting;
