import React, { useState, useRef } from 'react';
import { dummyChats } from '../data/dummyData';
import DemoDisclaimer from '../components/DemoDisclaimer';

interface ChatMessage {
  id: string;
  text: string;
  sender: string;
  timestamp: string;
  files?: File[];
}

const ChatRoom = () => {
  const [selectedChat, setSelectedChat] = useState<typeof dummyChats[0] | null>(null);
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [files, setFiles] = useState<File[]>([]);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const initializeChat = (chat: typeof dummyChats[0]) => {
    setSelectedChat(chat);
    setMessages([
      {
        id: '1',
        text: chat.message,
        sender: chat.sender,
        timestamp: chat.timestamp
      }
    ]);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setFiles(Array.from(e.target.files));
    }
  };

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if ((!newMessage.trim() && files.length === 0) || !selectedChat) return;

    const message: ChatMessage = {
      id: Date.now().toString(),
      text: newMessage,
      sender: 'You',
      timestamp: new Date().toLocaleTimeString(),
      files: files.length > 0 ? files : undefined
    };

    setMessages([...messages, message]);
    setNewMessage('');
    setFiles([]);
  };

  if (selectedChat) {
    return (
      <div style={{ 
        padding: '20px', 
        color: 'white',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column'
      }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          marginBottom: '20px'
        }}>
          <button
            onClick={() => setSelectedChat(null)}
            style={{
              background: 'none',
              border: 'none',
              color: 'white',
              fontSize: '20px',
              marginRight: '15px'
            }}
          >
            ←
          </button>
          <h3 style={{ margin: 0 }}>{selectedChat.sender}</h3>
        </div>

        <div style={{
          flex: 1,
          overflowY: 'auto',
          marginBottom: '20px'
        }}>
          {messages.map((msg) => (
            <div
              key={msg.id}
              style={{
                backgroundColor: msg.sender === 'You' ? '#007AFF' : '#333',
                padding: '10px',
                borderRadius: '10px',
                marginBottom: '10px',
                maxWidth: '80%',
                alignSelf: msg.sender === 'You' ? 'flex-end' : 'flex-start'
              }}
            >
              <div>{msg.text}</div>
              <div style={{ fontSize: '12px', color: '#999' }}>{msg.timestamp}</div>
            </div>
          ))}
        </div>

        <div style={{ marginTop: 'auto', paddingTop: '20px' }}>
          {files.length > 0 && (
            <div style={{
              display: 'flex',
              flexWrap: 'wrap',
              gap: '10px',
              padding: '10px',
              backgroundColor: '#333',
              borderRadius: '5px',
              marginBottom: '10px'
            }}>
              {files.map((file, index) => (
                <div 
                  key={index}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '5px',
                    backgroundColor: '#444',
                    padding: '5px 10px',
                    borderRadius: '5px',
                    fontSize: '14px'
                  }}
                >
                  {file.type.startsWith('image/') ? '🖼️' : '📎'} {file.name}
                  <button
                    onClick={() => setFiles(files.filter((_, i) => i !== index))}
                    style={{
                      background: 'none',
                      border: 'none',
                      color: '#999',
                      cursor: 'pointer',
                      padding: '0 5px'
                    }}
                  >
                    ✕
                  </button>
                </div>
              ))}
            </div>
          )}

          <form onSubmit={handleSendMessage} style={{ display: 'flex', gap: '10px' }}>
            <div style={{ flex: 1 }}>
              <textarea
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type a message..."
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '5px',
                  border: '1px solid #333',
                  backgroundColor: '#222',
                  color: 'white',
                  resize: 'none',
                  minHeight: '40px',
                  maxHeight: '120px'
                }}
                onKeyPress={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSendMessage(e);
                  }
                }}
              />
            </div>

            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileSelect}
              multiple
              style={{ display: 'none' }}
            />
            
            <button
              type="button"
              onClick={() => fileInputRef.current?.click()}
              style={{
                padding: '10px',
                backgroundColor: '#333',
                border: 'none',
                borderRadius: '5px',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              📎
            </button>

            <button
              type="submit"
              style={{
                padding: '10px 20px',
                backgroundColor: '#007AFF',
                border: 'none',
                borderRadius: '5px',
                color: 'white'
              }}
            >
              Send
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', color: 'white' }}>
      <DemoDisclaimer />
      <div style={{ marginBottom: '20px' }}>
        <input 
          type="text" 
          placeholder="Search..."
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #333',
            backgroundColor: '#222',
            color: 'white'
          }}
        />
      </div>

      {dummyChats.map((chat) => (
        <div 
          key={chat.id}
          onClick={() => initializeChat(chat)}
          style={{
            backgroundColor: '#222',
            borderRadius: '10px',
            padding: '15px',
            marginBottom: '15px',
            cursor: 'pointer'
          }}
        >
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
            <h3 style={{ margin: 0 }}>{chat.sender}</h3>
            <span>{chat.taskTitle}</span>
            {chat.unreadCount > 0 && (
              <span style={{ 
                backgroundColor: '#007AFF',
                borderRadius: '50%',
                padding: '2px 8px'
              }}>
                {chat.unreadCount}
              </span>
            )}
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <p style={{ margin: 0 }}>{chat.message}</p>
            <span style={{ color: '#666' }}>{chat.timestamp}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ChatRoom;
