import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icon
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

interface LocationPickerProps {
  onLocationSelect: (location: string) => void;
}

interface MapClickHandlerProps {
  onLocationSelect: (location: string) => void;
  setPosition: (position: [number, number]) => void;
}

// This component handles map clicks
const MapClickHandler = ({ onLocationSelect, setPosition }: MapClickHandlerProps) => {
  useMapEvents({
    click: (e) => {
      const { lat, lng } = e.latlng;
      setPosition([lat, lng]);
      onLocationSelect(`${lat}, ${lng}`);
    },
  });
  return null;
};

const LocationPicker = ({ onLocationSelect }: LocationPickerProps) => {
  const [position, setPosition] = useState<[number, number] | null>(null);

  const handleGetCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((location) => {
        const newPosition: [number, number] = [location.coords.latitude, location.coords.longitude];
        setPosition(newPosition);
        onLocationSelect(`${newPosition[0]}, ${newPosition[1]}`);
      });
    }
  };

  return (
    <div>
      <button
        type="button"
        onClick={handleGetCurrentLocation}
        style={{
          width: '100%',
          padding: '10px',
          backgroundColor: '#333',
          border: 'none',
          borderRadius: '5px',
          color: 'white',
          marginBottom: '10px',
          cursor: 'pointer'
        }}
      >
        üìç Use Current Location
      </button>

      <div style={{ height: '300px', borderRadius: '10px', overflow: 'hidden' }}>
        <MapContainer
          center={position || [51.505, -0.09]} // Default to London if no position
          zoom={13}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <MapClickHandler onLocationSelect={onLocationSelect} setPosition={setPosition} />
          {position && <Marker position={position} />}
        </MapContainer>
      </div>
    </div>
  );
};

export default LocationPicker;
