import { create } from 'zustand';
import { dummyTasks, dummyChats } from '../data/dummyData';
import { Task } from '../types';

interface Achievement {
  id: string;
  title: string;
  description: string;
  progress: number;
  reward: number;
  icon: string;
}

interface Profile {
  name: string;
  wallet: number;
  achievements: Achievement[];
  taskHistory: Task[];
  rating: number;
}

interface Chat {
  id: string;
  sender: string;
  taskTitle: string;
  message: string;
  timestamp: string;
  language: string;
  unreadCount: number;
}

interface TaskState {
  tasks: Task[];
  chats: Chat[];
  readMessages: string[];
  profile: Profile;
  addTask: (task: Omit<Task, 'id'>) => void;
  addChat: (chat: Chat) => void;
  markChatAsRead: (chatId: string) => void;
}

// Add dummy profile data
const dummyProfile: Profile = {
  name: "John Doe",
  wallet: 6000,
  achievements: [
    {
      id: "1",
      title: "First Task Complete",
      description: "Completed your first task",
      progress: 100,
      reward: 50,
      icon: "üéØ"
    },
    {
      id: "2",
      title: "Super Helper",
      description: "Helped 10 people",
      progress: 80,
      reward: 100,
      icon: "‚≠ê"
    },
    {
      id: "3",
      title: "Quick Responder",
      description: "Responded to 5 tasks within an hour",
      progress: 60,
      reward: 75,
      icon: "‚ö°"
    }
  ],
  taskHistory: dummyTasks.slice(0, 3),
  rating: 4.8
};

export const useTaskStore = create<TaskState>((set) => ({
  tasks: dummyTasks,
  chats: dummyChats,
  readMessages: [],
  profile: dummyProfile,
  addTask: (newTask) => set((state) => ({
    tasks: [
      {
        ...newTask,
        id: String(state.tasks.length + 1),
        status: 'open'
      },
      ...state.tasks
    ]
  })),
  addChat: (newChat) => set((state) => {
    const existingChat = state.chats.find(chat => 
      chat.sender === newChat.sender && 
      chat.taskTitle === newChat.taskTitle
    );
    if (existingChat) {
      return state;
    }
    return {
      chats: [newChat, ...state.chats]
    };
  }),
  markChatAsRead: (chatId: string) => set((state) => ({
    readMessages: [...state.readMessages, chatId],
    chats: state.chats.map(chat => 
      chat.id === chatId 
        ? { ...chat, unreadCount: 0 }
        : chat
    )
  }))
}));
