import { create } from 'zustand';
import { dummyTasks } from '../data/dummyData';
import { Task } from '../types';

interface TaskState {
  tasks: Task[];
  addTask: (task: Omit<Task, 'id' | 'status' | 'postedBy' | 'language' | 'flag'>) => void;
}

export const useTaskStore = create<TaskState>((set) => ({
  tasks: dummyTasks,
  addTask: (newTask) => set((state) => ({
    tasks: [
      {
        ...newTask,
        id: String(state.tasks.length + 1),
        status: 'open',
        postedBy: 'Demo User',
        flag: 'us',
        language: 'English'
      } as Task,
      ...state.tasks
    ]
  }))
}));
